import axios from "axios"
import { registerMessageHandler } from 'axios-chrome-messaging-adapter';

const APIURL = "http://127.0.0.1:5000"
// register the adapter message hanlder
registerMessageHandler();

async function sendReq(q) {
  return await axios.post(`${APIURL}/api/model`, {q})
}

chrome.tabs.onUpdated.addListener(async function
  (tabId, changeInfo, tab) {
    // read changeInfo data and do something with it (like read the url)
    if (changeInfo.url) {
      // chrome.storage.local.set({url: changeInfo.url});
      // chrome.storage.local.get(['url'], function(res) {
      //   console.log(res);
      // });
      let res = sendReq(changeInfo.url)
      console.log(res);
      // chrome.storage.local.set({xss: res});
    }
  }
);
