import { requestMixin } from '@/mixins';
export default {
  name: 'popupView',
  mixins: [requestMixin],

  data() {
    return {
      resData: 'loading...'
    };
  },

  methods: {
    async init() {
      chrome.storage.local.get(['xss'], function (res) {
        if (res !== null) {
          this.resData = res.xss;
        }
      });

      if (window.localStorage.getItem('xss') !== null) {
        this.resData = window.localStorage.getItem('xss');
      }

      if (this.resData === null || this.resData === 'loading...') {
        try {
          let q = window.location.href;
          this.resData = q; // const { data } = await requestMixin.methods.getXssRequest(q);
          // this.resData = data;
        } catch (e) {
          console.error(e);
        }
      }

      alert(this.resData);
    }

  },

  created() {
    this.init();
  }

};
